import Vue from 'vue'
import VueRouter from 'vue-router'
Vue.use(VueRouter)

import Index          from '@/views/index'
import Home           from '@/views/home'
import Login          from '@/views/login'
import NotFound       from '@/views/404'
import UpdatePassword from '@/views/update-password'
import Pay            from '@/views/pay'
import Register       from '@/views/register'
import Center         from '@/views/center'

// 模块列表
import MeishiList     from '@/views/modules/meishijianshang/list'
import YonghuList     from '@/views/modules/yonghu/list'
import DiscussList    from '@/views/modules/discussmeishijianshang/list'
import StoreupList    from '@/views/modules/storeup/list'
import ConfigList     from '@/views/modules/config/list'
import FriendsList    from '@/views/modules/wodehaoyou/list'

const routes = [
  {
    path: '/index',
    name: 'Index',      // 改为英文唯一标识
    component: Index,
    children: [
      // 默认打开 /index 时，渲染 Home
      {
        path: '',
        name: 'Home',
        component: Home,
        meta: { icon: '', title: '首页' }
      },
      {
        path: 'updatePassword',
        name: 'UpdatePassword',
        component: UpdatePassword,
        meta: { title: '修改密码' }
      },
      {
        path: 'pay',
        name: 'Pay',
        component: Pay,
        meta: { title: '支付' }
      },
      {
        path: 'center',
        name: 'Center',
        component: Center,
        meta: { title: '个人信息' }
      },
      {
        path: 'meishijianshang',
        name: 'MeishiList',
        component: MeishiList,
        meta: { title: '美食鉴赏' }
      },
      {
        path: 'yonghu',
        name: 'YonghuList',
        component: YonghuList,
        meta: { title: '用户管理' }
      },
      {
        path: 'discussmeishijianshang',
        name: 'DiscussList',
        component: DiscussList,
        meta: { title: '美食鉴赏评论' }
      },
      {
        path: 'storeup',
        name: 'StoreupList',
        component: StoreupList,
        meta: { title: '我的收藏' }
      },
      {
        path: 'config',
        name: 'ConfigList',
        component: ConfigList,
        meta: { title: '轮播图管理' }
      },
      {
        path: 'wodehaoyou',
        name: 'FriendsList',
        component: FriendsList,
        meta: { title: '我的好友' }
      }
    ]
  },
  {
    path: '/login',
    name: 'Login',
    component: Login
  },
  {
    path: '/register',
    name: 'Register',
    component: Register
  },
  // 根路径直接重定向到 /index
  {
    path: '/',
    redirect: '/index'
  },
  // 兜底 404
  {
    path: '*',
    component: NotFound
  }
]

export default new VueRouter({
  mode: 'hash',
  routes
})
