import Vue from 'vue';
//配置路由
import VueRouter from 'vue-router'
Vue.use(VueRouter);
//1.创建组件
import Index from '@/views/index'
import Home from '@/views/home'
import Login from '@/views/login'
import NotFound from '@/views/404'
import UpdatePassword from '@/views/update-password'
import pay from '@/views/pay'
import register from '@/views/register'
import center from '@/views/center'
    import meishijianshang from '@/views/modules/meishijianshang/list'
    import yonghu from '@/views/modules/yonghu/list'
    import discussmeishijianshang from '@/views/modules/discussmeishijianshang/list'
    import storeup from '@/views/modules/storeup/list'
    import config from '@/views/modules/config/list'
    import wodehaoyou from '@/views/modules/wodehaoyou/list'


//2.配置路由   注意：名字
const routes = [{
    path: '/index',
    name: '首页',
    component: Index,
    children: [{
      // 这里不设置值，是把main作为默认页面
      path: '/#/index',
      name: '首页',
      component: Home,
      meta: {icon:'', title:'center'}
    }, {
      path: '/updatePassword',
      name: '修改密码',
      component: UpdatePassword,
      meta: {icon:'', title:'updatePassword'}
    }, {
      path: '/pay',
      name: '支付',
      component: pay,
      meta: {icon:'', title:'pay'}
    }, {
      path: '/center',
      name: '个人信息',
      component: center,
      meta: {icon:'', title:'center'}
    }
          ,{
	path: '/meishijianshang',
        name: '美食鉴赏',
        component: meishijianshang
      }
          ,{
	path: '/yonghu',
        name: '用户',
        component: yonghu
      }
          ,{
	path: '/discussmeishijianshang',
        name: '美食鉴赏评论',
        component: discussmeishijianshang
      }
          ,{
	path: '/storeup',
        name: '我的收藏管理',
        component: storeup
      }
          ,{
	path: '/config',
        name: '轮播图管理',
        component: config
      }
          ,{
	path: '/wodehaoyou',
        name: '我的好友',
        component: wodehaoyou
      }
        ]
  },
  {
    path: '/login',
    name: 'login',
    component: Login,
    meta: {icon:'', title:'login'}
  },
  {
    path: '/register',
    name: 'register',
    component: register,
    meta: {icon:'', title:'register'}
  },
  {
    path: '/#/index',
    name: '首页',
    redirect: '/index'
  }, /*默认跳转路由*/
  {
    path: '*',
    component: NotFound
  }
]
//3.实例化VueRouter  注意：名字
const router = new VueRouter({
  mode: 'hash',
  /*hash模式改为history*/
  base: process.env.NODE_ENV === 'production'
    ? '/springboot35l3z/admin/dist/'   // —— 指定生产环境下的前缀
    : '/',                              // —— 开发环境仍用根目录
  routes // （缩写）相当于 routes: routes
})

export default router;
